class PostComments{constructor(e){this.postId=e,this.postContainer=$("#post-"+e),this.newCommentForm=$(`#post-${e}-comments-form`),this.createComment(e);let t=this;$(" .delete-comment-button",this.postContainer).each((function(){t.deleteComment($(this))}))}createComment(e){let t=this;this.newCommentForm.submit((function(n){n.preventDefault();$.ajax({type:"post",url:"/comments/create",data:$(this).serialize(),success:function(n){let o=t.newCommentDom(n.data.comment);$("#post-comments-"+e).prepend(o),t.deleteComment($(" .delete-comment-button",o)),new ToggleLike($(" .toggle-like-button",o)),new Noty({theme:"relax",text:"Comment published!",type:"success",layout:"topRight",timeout:1500}).show()},error:function(e){console.log(e.responseText)}})}))}newCommentDom(e){return $(`<li id="comment-${e._id} class="comments-list">\n                        <p>\n                            <small id="comment-name">\n                                ${e.user.name}\n                            </small>\n\n                            <small class="delete-comment">\n                                <a class="delete-comment-button" href="/comments/destroy/${e._id}"><i class="fad fa-trash"></i></a>\n                            </small>\n                            <br>\n                    \n                            <small class="comments-likes">\n                            \n                                <a class="toggle-like-button" data-likes="0" href="/likes/toggle/?id=${e._id}&type=Comment">\n                                    <i class="fas fa-thumbs-up"></i>\n                                    <small id="thumps-up">\n                                        0 Likes\n                                    </small>\n                                </a>\n                            \n                            </small>\n                            ${e.content}\n                        </p>    \n\n                </li>`)}deleteComment(e){$(e).click((function(t){t.preventDefault(),$.ajax({type:"get",url:$(e).prop("href"),success:function(e){$("#comment-"+e.data.comment_id).remove(),new Noty({theme:"relax",text:"Comment Deleted",type:"success",layout:"topRight",timeout:1500}).show()},error:function(e){console.log(e.responseText)}})}))}}