class ToggleLike{constructor(e){this.toggler=e,this.toggleLike()}toggleLike(){$(this.toggler).click((function(e){e.preventDefault();let t=this,l=!0;$.ajax({type:"POST",url:$(t).attr("href")}).done((function(e){let i=parseInt($(t).attr("data-likes"));console.log(i),1==e.data.deleted?(i-=1,l=!1):i+=1,l?$(t).addClass("like-red"):$(t).removeClass("like-red"),$(t).attr("data-likes",i),$(t).children("small").text(i+" Likes")})).fail((function(e){console.log("error in completing the request")}))}))}}